<template>
  <div
    v-if="isVisible"
    class="fixed inset-0 bg-black bg-opacity-50 flex mx-auto items-center justify-center z-50 backdrop-blur-sm"
    @click="closeModal"
  >
    <div
      class="bg-[#f5f7e7]  p-6 rounded-lg mx-auto w-full md-max:w-[82%] sm-max:w-[80%] md-max:h-auto xs-max:w-[255px] shadow-lg relative"
      @click.stop
    >
      <!-- Content Wrapper -->
      <div class="flex flex-col items-start">
        <p class="text-[11px] font-noto font-normal text-black leading-[19.8px] pb-[24px] text-start">
          Upgrade to the Pro plan for unlimited downloads and help support our platform and contributors.
          By choosing this plan, you actively contribute to preserving the art and culture we share,
          while gaining unrestricted access to our full collection of photos.
        </p>
        
        <!-- Pro Plan Features -->
        <h2 class="text-[13px] font-noto font-bold mb-4 leading-[23.4px] text-center">
          Pro Plan includes:
        </h2>
        <ul class="text-[10px] leading-[18px] text-gray-700 mb-6 space-y-2">
          <li class="flex items-center gap-2">
            <span class="flex justify-center items-center w-[18px] h-[18px] rounded-full bg-[#DBF226]">
              <img src="@/assets/logo/tick.svg" alt="tick icon" class="w-[16px] h-[16px]" />
            </span>
            Unlimited downloads
          </li>
          <li class="flex items-center gap-2">
            <span class="flex justify-center items-center w-[18px] h-[18px] rounded-full bg-[#DBF226]">
              <img src="@/assets/logo/tick.svg" alt="tick icon" class="w-[16px] h-[16px]" />
            </span>
            Priority access to support
          </li>
          <li class="flex items-center gap-2">
            <span class="flex justify-center items-center w-[18px] h-[18px] rounded-full bg-[#DBF226]">
              <img src="@/assets/logo/tick.svg" alt="tick icon" class="w-[16px] h-[16px]" />
            </span>
            Commercial license included
          </li>
        </ul>

        <!-- Pricing Options -->
        <div class="flex flex-col md-max:gap-6 w-full">
          <!-- Monthly Plan -->
          <div class="flex flex-col items-center md-max:gap-6">
            <p class="text-[15px] text-gray-700">Monthly Plan</p>
            <p class="text-[15px] font-normal text-black flex items-baseline gap-1">
              USD $ <span class="text-[30px] font-bold leading-[33px]">2.99</span>
              <span class="text-gray-500">/month</span>
            </p>
            <button
              :class="[
                'w-full border border-black/50 h-[61px] mt-6 text-[17px] font-medium px-8 py-3 rounded-[10px] shadow-lg transition-all duration-300',
                isSelected ? 'bg-black text-white ' : 'bg-black text-white hover:bg-white hover:text-black'
              ]"
              @click="toggleSelection"
            >
              Get monthly
            </button>
          </div>

          <!-- Yearly Plan -->
          <div class="flex flex-col items-center md-max:gap-6">
             <div class="flex flex-row space-x-[14px] items-center">
              <p class="text-[15px] text-[##4C4C4C] font-noto leading-[27px] font-normal">Yearly Plan</p>
              <div class="bg-[#DBF226] text-black text-[10px] leading-[18px] font-bold px-3.5 py-1 rounded-md">2 months free</div>
            </div>
            <p class="text-[15px] font-normal text-black flex items-baseline gap-1">
              USD $ <span class="text-[30px] font-bold leading-[33px]">35.88</span>
              <span class="text-gray-500">/year</span>
            </p>
            <button
            :class="{
              'bg-[#0D0D0D] text-white': isYearlySelected,
              'bg-white text-black hover:bg-[#EFF1D9] hover:shadow-[0px_4px_16px_0px_#DBF226]': !isYearlySelected
            }"
              class="w-full border border-black/50 h-[61px] mt-6  text-[17px] font-medium px-8 py-3 rounded-[10px] shadow-lg transition-all duration-300"
              @click="toggleYearlySelected"
              >
              Get yearly
            </button>
          </div>
          <p class="text-[10px] leading-[18px]  text-center text-gray-500">Tax excluded.</p>
        </div>

        <!-- Payment Methods -->
        <div class="flex xl-max:flex-wrap justify-center xl-max:justify-center gap-2 items-start mt-6 mx-auto">
            <span class="text-[10px] font-noto leading-[18px] text-[#000]">Payment Methods:</span>
