  <script>
  export default {
    data() {
      return {
        isVisible: false,
      };
    },
    setup() {
    // Reactive state for a generic selection toggle (if required)
    const isSelected = ref(false);
    // Reactive state for "Get yearly" button
    const isYearlySelected = ref(false);
    
    // Function to toggle the "Get yearly" selected state
    const toggleYearlySelected = () => {
      isYearlySelected.value = !isYearlySelected.value;
    };
    // Function to toggle the generic selection state
    const toggleSelection = () => {
      isSelected.value = !isSelected.value;
    };   
    return {
      isSelected,
      toggleSelection,
      isYearlySelected,
      toggleYearlySelected,
    };
  },
    methods: {
      showModal() {
        this.isVisible = true;
      },
      closeModal() {
        this.isVisible = false;
      },
    },
  };
  </script>
  